///////////////////\n                JQUERY:\n    ////////////////////////////////\n        1. INLINE:\n        &lt;script type=&quot;text/javascript&quot;&gt;\n            document.addEventListener(''DOMContentLoaded'',function(){\n                // Your JS Code HERE\n            })\n        &lt;/script&gt;    \n        \n        2. SRC:\n        &lt;script type=&quot;text/javascript&quot; defer src=&quot;PATH TO YOUR LINK&quot;&gt;&lt;/script&gt;\n    \n    \n    ////////////////////////////////\n                  CSS: \n    ////////////////////////////////                \n        1. INLINE:\n        &lt;style&gt;\n            // Your CSS Code HERE\n        &lt;/style&gt;\n\n        2. SRC:\n        &lt;link rel=&quot;stylesheet&quot; type=&quot;text/css&quot; href=&quot;PATH TO YOUR LINK&quot; &gt;\n--&gt;&lt;/p&gt;\n&lt;script type=&quot;text/javascript&quot;&gt;\n            document.addEventListener(''DOMContentLoaded'',function(){\n                // Your JS Code HERE\n                /**\n * Created by Kupletsky Sergey on 17.10.14.\n *\n * Material Sidebar (Profile menu)\n * Tested on Win8.1 with browsers: Chrome 37, Firefox 32, Opera 25, IE 11, Safari 5.1.7\n * You can use this sidebar in Bootstrap (v3) projects. HTML-markup like Navbar bootstrap component will make your work easier.\n * Dropdown menu and sidebar toggle button works with JQuery and Bootstrap.min.js\n */\n\n// Sidebar toggle\n//\n// -------------------\n$(document).ready(function() {\n    var overlay = $(''.sidebar-overlay'');\n\n    $(''.sidebar-toggle'').on(''click'', function() {\n        var sidebar = $(''#sidebar'');\n        sidebar.toggleClass(''open'');\n        if ((sidebar.hasClass(''sidebar-fixed-left'') || sidebar.hasClass(''sidebar-fixed-right'')) &amp;&amp; sidebar.hasClass(''open'')) {\n            overlay.addClass(''active'');\n        } else {\n            overlay.removeClass(''active'');\n        }\n    });\n\n    overlay.on(''click'', function() {\n        $(this).removeClass(''active'');\n        $(''#sidebar'').removeClass(''open'');\n    });\n\n});\n\n// Sidebar constructor\n//\n// -------------------\n$(document).ready(function() {\n\n    var sidebar = $(''#sidebar'');\n    var sidebarHeader = $(''#sidebar .sidebar-header'');\n    var sidebarImg = sidebarHeader.css(''background-image'');\n    var toggleButtons = $(''.sidebar-toggle'');\n\n    // Hide toggle buttons on default position\n   /* toggleButtons.css(''display'', ''none'');\n    $(''body'').css(''display'', ''table'');*/\n\n\n    // Sidebar position\n    $(''#sidebar-position'').change(function() {\n        var value = $( this ).val();\n        sidebar.removeClass(''sidebar-fixed-left sidebar-fixed-right sidebar-stacked'').addClass(value).addClass(''open'');\n        if (value == ''sidebar-fixed-left'' || value == ''sidebar-fixed-right'') {\n            $(''.sidebar-overlay'').addClass(''active'');\n        }\n        // Show toggle buttons\n        if (value != '''') {\n            toggleButtons.css(''display'', ''initial'');\n            $(''body'').css(''display'', ''initial'');\n        } else {\n            // Hide toggle buttons\n            toggleButtons.css(''display'', ''none'');\n            $(''body'').css(''display'', ''table'');\n        }\n    });\n\n    // Sidebar theme\n    $(''#sidebar-theme'').change(function() {\n        var value = $( this ).val();\n        sidebar.removeClass(''sidebar-default sidebar-inverse sidebar-colored sidebar-colored-inverse'').addClass(value)\n    });\n\n    // Header cover\n    $(''#sidebar-header'').change(function() {\n        var value = $(this).val();\n\n        $(''.sidebar-header'').removeClass(''header-cover'').addClass(value);\n\n        if (value == ''header-cover'') {\n            sidebarHeader.css(''background-image'', sidebarImg)\n        } else {\n            sidebarHeader.css(''background-image'', '''')\n        }\n    });\n});\n\n/**\n * Created by Kupletsky Sergey on 08.09.14.\n *\n * Add JQuery animation to bootstrap dropdown elements.\n */\n\n(function($) {\n    var dropdown = $(''.dropdown'');\n\n    // Add slidedown animation to dropdown\n    dropdown.on(''show.bs.dropdown'', function(e){\n        $(this).find(''.dropdown-menu'').first().stop(true, true).slideDown();\n    });\n\n    // Add slideup animation to dropdown\n    dropdown.on(''hide.bs.dropdown'', function(e){\n        $(this).find(''.dropdown-menu'').first().stop(true, true).slideUp();\n    });\n})(jQuery);\n\n\n\n(function(removeClass) {\n\n	jQuery.fn.removeClass = function( value ) {\n		if ( value &amp;&amp; typeof value.test === &quot;function&quot; ) {\n			for ( var i = 0, l = this.length; i &lt; l; i++ ) {\n				var elem = this[i];\n				if ( elem.nodeType === 1 &amp;&amp; elem.className ) {\n					var classNames = elem.className.split( /s+/ );\n\n					for ( var n = classNames.length; n--; ) {\n						if ( value.test(classNames[n]) ) {\n							classNames.splice(n, 1);\n						}\n					}\n					elem.className = jQuery.trim( classNames.join(&quot; &quot;) );\n				}\n			}\n		} else {\n			removeClass.call(this, value);\n		}\n		return this;\n	}\n\n})(jQuery.fn.removeClass);\n            })\n        &lt;/script&gt;&lt;script type=&quot;text/javascript&quot; defer=&quot;&quot; src=&quot;https://sdk.clarifai.com/js/clarifai-latest.js&quot;&gt;&lt;/script&gt;&lt;/section&gt;&lt;/div&gt;\n    &lt;/div&gt;&lt;/section&gt;', 1, 1, 0, 0, 1, 2);
